QPushButton {
    /* 基础样式 - 使用您指定的颜色 */
    background-color: #66929ff5;  /* 主背景色 */
    border: 2px solid #5a82d5;   /* 稍深的边框色 */
    border-radius: 15px;
    color: #ffffff;              /* 白色文字 */
    font-size: 18px;
    font-weight: medium;
    padding: 8px 20px;
    min-width: 60px;

    /* 渐变效果 - 基于您颜色的变体 */
    background: qlineargradient(
        x1:0, y1:0, x2:0, y2:1,
        stop:0 #769ff5, stop:1 #66929ff5
    );

    /* 文字效果 */
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
}

/* 悬停效果 - 稍微变亮 */
QPushButton:hover {
    background: qlineargradient(
        x1:0, y1:0, x2:0, y2:1,
        stop:0 #86afff, stop:1 #76a2ff
    );
    border-color: #6a92e5;
}

/* 按下效果 - 加深颜色 */
QPushButton:pressed {
    background: qlineargradient(
        x1:0, y1:0, x2:0, y2:1,
        stop:0 #5682e5, stop:1 #4672d5
    );
    padding: 9px 19px 7px 21px;
    border-color: #4a72c5;
}

/* 禁用状态 - 灰色调 */
QPushButton:disabled {
    background: #c0c8d0;
    border-color: #b0b8c0;
    color: #f0f0f0;
}

/* 焦点状态 - 发光效果 */
QPushButton:focus {
    outline: none;
    border: 2px solid #a0c0ff;
    box-shadow: 0 0 5px rgba(160, 192, 255, 0.7);
}
